                                                                    <strong>0</strong>
                                                                    <small class="text-danger ms-2">
                                                                        <i class="fas fa-arrow-down fa-sm pe-1"></i></small>
                                                                </h5>

                                                                <hr />

                                                                <p class="text-uppercase text-muted small mb-2">
                                                                    Previous period
                                                                </p>
                                                                <h5 class="text-muted mb-0"></h5>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </section>

                                            <section>
